package controller;

import static org.mockito.ArgumentMatchers.refEq;
import static org.mockito.Mockito.never;
import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import javax.servlet.RequestDispatcher;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;
import model.*;

import org.junit.*;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.MethodOrderer;
import org.junit.jupiter.api.Order;
import org.junit.jupiter.api.TestMethodOrder;
import org.mockito.Mockito;

import model.Dao;
import model.Usuario;

@TestMethodOrder(MethodOrderer.MethodName.class)
public class TestController {

    HttpServletRequest request;
    HttpServletResponse response;
    HttpSession session;
    RequestDispatcher rd;
    Usuario usuario;
    Dao dao = new Dao();
    TestDao td = new TestDao();
    Controller controller = new Controller();

    @Before
    public void init() {
        try {
            // Simula o objeto, não vão ser feitas requisições e respostas no navegador na
            // pratica
            request = Mockito.mock(HttpServletRequest.class);
            response = Mockito.mock(HttpServletResponse.class);
            rd = Mockito.mock(RequestDispatcher.class);
            session = Mockito.mock(HttpSession.class);
            usuario = Mockito.mock(Usuario.class);

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    @Order(1)
    @DisplayName("Teste de cadastro")
    public void testA() {
        try {
            when(request.getParameter("nome")).thenReturn("Teste Form");
            when(request.getParameter("email")).thenReturn("testeMock@mail.com");
            when(request.getParameter("senha")).thenReturn("123456");
            when(request.getParameter("telefone")).thenReturn("1234-5678");
            when(request.getParameter("cidade")).thenReturn("Salvador");
            when(request.getParameter("estado")).thenReturn("BA");
            when(request.getParameter("nomeAnimal")).thenReturn("Cavalo");
            when(request.getParameter("descricaoAnimal")).thenReturn("Cavaloooooooo");
            // Imagem é salva em base64 no banco
            when(request.getParameter("imagem")).thenReturn(
                    "data:image/jpeg;base64,");

            // Define o valor do objeto simulado quando o método getRequestDispatcher() for
            // chamado com o argumento correto usando o método when() do Mockito
            when(request.getRequestDispatcher("cadastro.jsp")).thenReturn(rd);

            controller.create(request, response);

            // Verifica se o método foi chamado, nesse caso após cadastrar no banco
            // é encaminhado para o usuário uma mensagem de confirmação do cadastro
            verify(request).setAttribute("mensagem", "sucesso");

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    @Order(2)
    @DisplayName("Teste de validação de e-mail existente no banco de dados ao realizar o cadastro")
    public void testB() {
        try {
            when(request.getParameter("nome")).thenReturn("Teste Form 2");
            when(request.getParameter("email")).thenReturn("testeMock@mail.com");
            when(request.getParameter("senha")).thenReturn("123456");
            when(request.getParameter("telefone")).thenReturn("1234-5678");
            when(request.getParameter("cidade")).thenReturn("Salvador");
            when(request.getParameter("estado")).thenReturn("BA");
            when(request.getParameter("nomeAnimal")).thenReturn("Cavalo");
            when(request.getParameter("descricaoAnimal")).thenReturn("Cavaloooooooo");
            when(request.getParameter("imagem")).thenReturn("");

            when(request.getRequestDispatcher("cadastro.jsp")).thenReturn(rd);

            controller.create(request, response);

            // O conteúdo precisa ser diferente do anterior, pois o e-mail foi cadastrando
            // anteriormente
            verify(request).setAttribute("mensagem", "existente");

            // Alterando o email para usar em um teste posterior de enviar email
            when(request.getParameter("email")).thenReturn("testeMockTwo@mail.com");
            controller.create(request, response);

        } catch (Exception e) {
            e.printStackTrace();
        }

    }

    @Test
    @Order(3)
    @DisplayName("Teste de logar no sistema")
    public void testC() {
        try {

            when(request.getParameter("email")).thenReturn("testeMock@mail.com");
            when(request.getParameter("senha")).thenReturn("123456");

            when(request.getSession()).thenReturn(session);

            when(request.getRequestDispatcher("animalife.jsp")).thenReturn(rd);

            controller.select(request, response);

            // Se o método nunca foi chamado, logo o login foi realizado com sucesso
            verify(request, never()).setAttribute("mensagem", "erro");

            // Se o e-mail e senha estiverem corretos será criada uma sessão
            verify(session).setAttribute("usuarioLogado", refEq(usuario));

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    @Order(4)
    @DisplayName("Teste de tentar entrar com a senha incorreta")
    public void testD() {
        try {
            when(request.getParameter("email")).thenReturn("testeMockShazam@mail.com");
            when(request.getParameter("senha")).thenReturn("");

            when(request.getSession()).thenReturn(session);

            when(request.getRequestDispatcher("index.jsp")).thenReturn(rd);

            controller.select(request, response);

            verify(request).setAttribute("mensagem", "erro");

            verify(session, never()).setAttribute("usuarioLogado", refEq(usuario));
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    @Order(5)
    @DisplayName("Teste de enviar e-mail com uma nova senha para o usuário cadastrado")
    public void testE() {

        try {
            when(request.getParameter("email")).thenReturn("testeMockTwo@mail.com");

            when(request.getRequestDispatcher("recuperarConta.jsp")).thenReturn(rd);

            controller.modificarSenha(request, response);

            verify(request).setAttribute("email", "enviado");

        } catch (Exception e) {
            e.printStackTrace();

        }

    }

    @Test
    @Order(6)
    @DisplayName("Teste de validação ao inserir um e-mail inexistente na base de dados para gerar uma nova senha")
    public void testF() {

        try {
            when(request.getParameter("email")).thenReturn("inexistente@mail.com.br");

            when(request.getRequestDispatcher("recuperarConta.jsp")).thenReturn(rd);

            controller.modificarSenha(request, response);

            verify(request).setAttribute("email", "invalido");

        } catch (Exception e) {
            e.printStackTrace();

        }

    }

    @Test
    @Order(7)
    @DisplayName("Teste de pesquisar um animal")
    public void testG() {
        try {

            when(request.getParameter("nomeAnimal")).thenReturn("Cavalo");

            when(request.getRequestDispatcher("animalifeResultado.jsp")).thenReturn(rd);

            controller.selecionarAnimal(request, response);

            // Se não retorna nenhum contéudo dentro do atributo, os dados foram enviados
            // com sucesso
            verify(request).setAttribute("retorno", "");

        } catch (Exception e) {
            e.printStackTrace();
        }

    }

    @Test
    @Order(8)
    @DisplayName("Teste de atualizar as informações do animal")
    public void testH() {
        try {
            when(request.getParameter("id")).thenReturn(td.idAtual());
            when(request.getParameter("nomeAnimal")).thenReturn("Cavalo Árabe");
            when(request.getParameter("descricaoAnimal"))
                    .thenReturn("é uma raça equina originada na Península Arábica");

            when(request.getRequestDispatcher("alterarInformacoes.jsp")).thenReturn(rd);

            controller.alterarInfoAnimal(request, response);

            verify(request).setAttribute("retorno", "true");

        } catch (Exception e) {
            e.printStackTrace();
        }

    }

    @Test
    @Order(9)
    @DisplayName("Teste de atualização da senha feita pelo próprio usuário, onde a senha está correta")
    public void testI() {
        try {

            when(request.getParameter("email")).thenReturn("testeMock@mail.com");
            when(request.getParameter("senhaAtual")).thenReturn("123456");
            when(request.getParameter("novaSenha")).thenReturn("senhaNova@1015");

            when(request.getRequestDispatcher("modificarSenha.jsp")).thenReturn(rd);

            controller.atualizarSenha(request, response);

            verify(request).setAttribute("retorno", "true");

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    @Order(10)
    @DisplayName("Teste de atualização da senha feita pelo próprio usuário, onde a senha está incorreta")
    public void testJ() {
        try {

            when(request.getParameter("email")).thenReturn("testeMock@mail.com");
            when(request.getParameter("senhaAtual")).thenReturn("senhaErrada");
            when(request.getParameter("novaSenha")).thenReturn("senhaNova@1015");

            when(request.getRequestDispatcher("modificarSenha.jsp")).thenReturn(rd);

            controller.atualizarSenha(request, response);

            verify(request).setAttribute("retorno", "false");

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    @Order(11)
    @DisplayName("Teste de deletar a conta")
    public void testK() {
        try {
            // Vai realizar a exclusão do primeiro cadastro realizado
            String primeiroCadastro = td.idAtual();
            primeiroCadastro = Integer.toString(Integer.parseInt(primeiroCadastro) - 1);
            when(request.getParameter("id")).thenReturn(primeiroCadastro);
            when(request.getRequestDispatcher("index.jsp")).thenReturn(rd);

            controller.deletarCadastro(request, response);

            verify(request).setAttribute("retorno", "true");

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

}
